function detectmob(){return!!(navigator.userAgent.match(/Android/i)||navigator.userAgent.match(/webOS/i)||navigator.userAgent.match(/iPhone/i)||navigator.userAgent.match(/iPad/i)||navigator.userAgent.match(/iPod/i)||navigator.userAgent.match(/BlackBerry/i)||navigator.userAgent.match(/Windows Phone/i))}function setFooterPosition(){detectmob()&&(console.log("[news letter controller] focus detected on mobile device"),document.querySelector(".footer").style.display="none")}document.querySelector("body").focus(),document.querySelector("#email-input").addEventListener("focus",setFooterPosition());